// client/src/components/AddFlightTakeoffForm.jsx

import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Form.css';

export default function AddFlightTakeoffForm() {
  const [flightID, setFlightID] = useState('');
  const [msg, setMsg]           = useState('');
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/flight_takeoff', { flightID });
      // clear input
      //setFlightID('');
      // go to Flights page
      navigate('/flights');
    } catch (err) {
      setMsg(err.response?.data?.error || '❌ Error recording takeoff');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>flight_takeoff()</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Flight ID</label>
          <input
            name="flightID"
            value={flightID}
            onChange={e => setFlightID(e.target.value)}
            required
          />
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>

      {msg && <p className="error-text">{msg}</p>}
    </form>
  );
}
