import React, { useState } from 'react';
import axios from 'axios';

export default function AddAirplaneForm() {
  const [form, setForm] = useState({
    tail_num: '', airline_id: '', speed: '', seat_capacity: '', model: '', plane_type: ''
  });
  const [msg, setMsg] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/airplanes', form);
      setMsg('Airplane added successfully!');
    } catch (err) {
      setMsg(err.response?.data?.error || 'Error adding airplane.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Airplane</h2>
      {Object.keys(form).map(key => (
        <div key={key}>
          <label>{key}:</label>
          <input name={key} value={form[key]} onChange={handleChange} required />
        </div>
      ))}
      <button type="submit">Add</button>
      <p>{msg}</p>
    </form>
  );
}
