import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddAirplaneForm.css';

export default function AddPassengerBoardForm() {
  const navigate = useNavigate();
  const [flightID, setFlightID] = useState('');
  const [msg, setMsg] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('/api/passengers-board', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ flightID })
      });
      const out = await res.json();
      if (res.ok && out.status === 'success') {
        navigate('/view-passengers-board');        // auto‑switch
      } else {
        setMsg(`❌ ${out.error || 'Unknown error'}`);
      }
    } catch {
      setMsg('❌ Error connecting to server');
    }
  };

  return (
    <div className="form-container">
      <h2>passengers_board()</h2>
      <form onSubmit={handleSubmit}>
        <label>Flight ID:</label>
        <input value={flightID} onChange={e => setFlightID(e.target.value)} required />
        <button type="submit">Board</button>
      </form>
      {msg && <p className="message">{msg}</p>}
    </div>
  );
}
