import React, { useState } from 'react';
import axios from 'axios';
import './AddFlightTakeoffForm.css';


export default function AddFlightTakeoffForm() {
  const [form, setForm] = useState({
    airlineID: '',
    flight_num: '',
    depart_time: '',
  });
  const [msg, setMsg] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/flight-takeoff', form);
      setMsg('✅ Flight takeoff successful!');
    } catch (err) {
      setMsg(err.response?.data?.error || '❌ Error recording takeoff');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>flight_takeoff()</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Airline ID:</label>
          <input name="airlineID" value={form.airlineID} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Flight Number:</label>
          <input name="flight_num" value={form.flight_num} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Departure Time (YYYY-MM-DD HH:MM:SS):</label>
          <input name="depart_time" value={form.depart_time} onChange={handleChange} required />
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
