// client/src/components/RouteSummary.jsx
import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './Form.css';

export default function RouteSummary() {
  const [rows, setRows] = useState([])
  const [error, setError] = useState('')

  const fetchData = async () => {
    try {
      const res = await axios.get('/api/route_summary')
      setRows(res.data)
      setError('')
    } catch (err) {
      console.error('❌ Fetch route_summary error:', err.response?.data || err)
      setError('Failed to load route summary')
    }
  }

  useEffect(() => {
    fetchData()
  }, [])

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Route Summary</h2>
      <button onClick={fetchData} style={{ margin: '1rem 0' }}>
        🔄 Refresh
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>Route</th>
            <th># Legs</th>
            <th>Leg Sequence</th>
            <th>Total Distance</th>
            <th># Flights</th>
            <th>Flight List</th>
            <th>Airport Sequence</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.route}</td>
              <td>{r.num_legs}</td>
              <td>{r.leg_sequence}</td>
              <td>{r.route_length}</td>
              <td>{r.num_flights}</td>
              <td>{r.flight_list}</td>
              <td>{r.airport_sequence}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
