import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewPersons() {
  const [people, setPeople] = useState([]);
  const [error,  setError]  = useState('');

  const fetchPeople = () => {
    axios.get('/api/persons')
      .then(res => {
        setPeople(res.data);
        setError('');
      })
      .catch(err => {
        console.error('❌ Fetch persons error:', err.response?.data || err);
        setError('Failed to load people');
      });
  };

  useEffect(() => {
    fetchPeople();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>All People</h2>

      <button onClick={fetchPeople} style={{ margin: '1rem 0' }}>
        🔄 Refresh
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table border="1" cellPadding="5" cellSpacing="0">
        <thead>
          <tr>
            <th>PersonID</th>
            <th>First</th>
            <th>Last</th>
            <th>LocationID</th>
            <th>TaxID</th>
            <th>Experience</th>
            <th>Miles</th>
            <th>Funds</th>
          </tr>
        </thead>
        <tbody>
          {people.map((p, i) => (
            <tr key={i}>
              <td>{p.personID}</td>
              <td>{p.firstName}</td>
              <td>{p.lastName ?? ''}</td>
              <td>{p.locationID}</td>
              <td>{p.taxID}</td>
              <td>{p.experience}</td>
              <td>{p.miles ?? ''}</td>
              <td>{p.funds ?? ''}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
