import React, { useState } from 'react';
import axios from 'axios';

export default function OfferFlightForm() {
  const [form, setForm] = useState({
    flightID: '',
    routeID: '',
    support_airline: '',
    support_tail: '',
    progress: 0,
    next_time: '',
    cost: 0,
  });
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/offer_flight', form);
      alert('✅ Flight offered successfully');
      setError(null);
    } catch (e) {
      setError(e.response?.data?.error || e.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4 p-4 bg-white rounded shadow">
      <label className="block">
        Flight ID
        <input
          type="text" required
          className="w-full p-2 border"
          value={form.flightID}
          onChange={e => setForm(f => ({ ...f, flightID: e.target.value }))}
        />
      </label>
      <label className="block">
        Route ID
        <input
          type="text" required
          className="w-full p-2 border"
          value={form.routeID}
          onChange={e => setForm(f => ({ ...f, routeID: e.target.value }))}
        />
      </label>
      <label className="block">
        Support Airline
        <input
          type="text"
          className="w-full p-2 border"
          value={form.support_airline}
          onChange={e => setForm(f => ({ ...f, support_airline: e.target.value }))}
        />
      </label>
      <label className="block">
        Support Tail
        <input
          type="text"
          className="w-full p-2 border"
          value={form.support_tail}
          onChange={e => setForm(f => ({ ...f, support_tail: e.target.value }))}
        />
      </label>
      <label className="block">
        Progress
        <input
          type="number" required
          className="w-full p-2 border"
          value={form.progress}
          onChange={e => setForm(f => ({ ...f, progress: Number(e.target.value) }))}
        />
      </label>
      <label className="block">
        Next Time (HH:MM:SS)
        <input
          type="text" required
          className="w-full p-2 border"
          value={form.next_time}
          onChange={e => setForm(f => ({ ...f, next_time: e.target.value }))}
        />
      </label>
      <label className="block">
        Cost
        <input
          type="number" required
          className="w-full p-2 border"
          value={form.cost}
          onChange={e => setForm(f => ({ ...f, cost: Number(e.target.value) }))}
        />
      </label>
      <div className="col-span-2 flex justify-end space-x-2 mt-4">
        <button
          type="reset"
          className="px-4 py-2 bg-gray-300 rounded"
          onClick={() => setError(null)}
        >
          Cancel
        </button>
        <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded">
          Offer Flight
        </button>
      </div>
      {error && <p className="col-span-2 text-red-600">{error}</p>}
    </form>
  );
}
