// client/src/components/AddAirportForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AddAirportForm.css';

export default function AddAirportForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    airportID:    '',
    airportName:  '',
    city:         '',
    state:        '',
    country:      '',
    international:'',
    locationID:   ''
  });
  const [msg, setMsg] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();

    // ── Define nullify here, before using it ──
    const nullify = v => {
      if (v === null || v === undefined) return null;
      const t = String(v).trim().toUpperCase();
      return (t === '' || t === 'NULL') ? null : v;
    };

    const submission = {
      airportID:     form.airportID,
      airportName:   form.airportName,
      city:          form.city,
      state:         form.state,
      country:       form.country,
      locationID:    nullify(form.locationID)   // now optional
    };

    console.log('📤 Adding airport:', submission);

    try {
      await axios.post('/api/airports', submission);
      navigate('/view-airports');
    } catch (err) {
      console.error('❌ Add airport error:', err.response?.data || err);
      setMsg(err.response?.data?.error || 'Error adding airport');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>add_airport()</h2>
      <div className="form-grid">
        {/* Airport ID */}
        <div className="form-group">
          <label>Airport ID:</label>
          <input
            name="airportID"
            value={form.airportID}
            onChange={handleChange}
            required
          />
        </div>

        {/* Airport Name */}
        <div className="form-group">
          <label>Airport Name:</label>
          <input
            name="airportName"
            value={form.airportName}
            onChange={handleChange}
            required
          />
        </div>

        {/* City */}
        <div className="form-group">
          <label>City:</label>
          <input
            name="city"
            value={form.city}
            onChange={handleChange}
            required
          />
        </div>

        {/* State */}
        <div className="form-group">
          <label>State:</label>
          <input
            name="state"
            value={form.state}
            onChange={handleChange}
            required
          />
        </div>

        {/* Country */}
        <div className="form-group">
          <label>Country:</label>
          <input
            name="country"
            value={form.country}
            onChange={handleChange}
            required
          />
        </div>


        {/* Location ID (optional) */}
        <div className="form-group">
          <label>Location ID (optional):</label>
          <input
            name="locationID"
            value={form.locationID}
            onChange={handleChange}
            placeholder="Leave blank or type NULL"
          />
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Add</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
