// client/src/components/AddPassengerBoardForm.jsx
import React, { useState } from 'react';
import './AddAirplaneForm.css';  // your shared form CSS

export default function AddPassengerBoardForm() {
  const [flightID, setFlightID] = useState('');
  const [msg, setMsg] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');

    try {
      const res = await fetch('/api/passengers-board', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ flightID })
      });
      const out = await res.json();

      if (res.ok && out.status === 'success') {
        setMsg('✅ Passengers boarded successfully!');
        setFlightID('');   // clear form
      } else {
        setMsg(`❌ ${out.error || 'Unknown error'}`);
      }
    } catch {
      setMsg('❌ Unable to connect to server');
    }
  };

  return (
    <div className="form-container">
      <h2>passengers_board()</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Flight ID:</label>
          <input
            type="text"
            value={flightID}
            onChange={e => setFlightID(e.target.value)}
            required
          />
        </div>
        <div className="form-actions">
          <button type="submit">Board Passengers</button>
        </div>
      </form>
      {msg && (
        <p className={msg.startsWith('✅') ? 'success-message' : 'error-message'}>
          {msg}
        </p>
      )}
    </div>
  );
}
