import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function FlightsList() {
  const [flights, setFlights] = useState([]);
  const [error, setError] = useState(null);

  const loadFlights = () => {
    axios.get('/api/flights')
      .then(res => { setFlights(res.data); setError(null); })
      .catch(err => setError(err.message));
  };

  useEffect(() => { loadFlights() }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold mb-4">All Flights</h2>
      <button
        onClick={loadFlights}
        className="mb-4 px-4 py-2 bg-green-500 text-white rounded"
      >
        Refresh
      </button>
      {error && <p className="text-red-600">Error: {error}</p>}
      <ul className="list-disc pl-5">
        {flights.map((f, i) => (
          <li key={i}>
            ID: {f.flightID} | Route: {f.routeID} | Status: {f.airplane_status} | Next: {f.next_time} | Cost: {f.cost}
          </li>
        ))}
      </ul>
    </div>
  );
}
