// client/src/components/AlternateAirports.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function AlternateAirports() {
  const [rows, setRows]       = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState('');

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/alternate_airports');
      setRows(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to load alternate airports');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="view-container">
      <h2>Alternate Airports</h2>
      <button
        className="refresh-btn"
        onClick={fetchData}
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Refresh'}
      </button>
      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>City</th>
            <th>State</th>
            <th>Country</th>
            <th># Airports</th>
            <th>Codes</th>
            <th>Names</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((a, idx) => (
            <tr key={`${a.city}-${idx}`}>
              <td>{a.city}</td>
              <td>{a.state || '—'}</td>
              <td>{a.country}</td>
              <td>{a.num_airports}</td>
              <td>{a.airport_code_list}</td>
              <td>
                <ul className="cell-list">
                  {a.airport_names_list
                    .split(',')
                    .map((name, j) => <li key={j}>{name}</li>)}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
