// client/src/components/OfferFlightForm.jsx

import React, { useState } from 'react';
import axios from 'axios';
import './Form.css';

export default function OfferFlightForm() {
  const [form, setForm] = useState({
    flightID:       '',
    routeID:        '',
    support_airline:'',
    support_tail:   '',
    progress:       0,
    next_time:      '',
    cost:           0,
  });
  const [msg, setMsg] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({
      ...f,
      [name]: name === 'progress' || name === 'cost'
        ? Number(value)
        : value
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/offer_flight', form);
    } catch (err) {
      setMsg(err.response?.data?.error || 'Error offering flight');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>offer_flight()</h2>

      <div className="form-grid">
        <div className="form-group">
          <label>Flight ID</label>
          <input
            name="flightID"
            value={form.flightID}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Route ID</label>
          <input
            name="routeID"
            value={form.routeID}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Support Airline</label>
          <input
            name="support_airline"
            value={form.support_airline}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Support Tail</label>
          <input
            name="support_tail"
            value={form.support_tail}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label>Progress</label>
          <input
            name="progress"
            type="number"
            min="0"
            max="100"
            value={form.progress}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Next Time (HH:MM:SS)</label>
          <input
            name="next_time"
            value={form.next_time}
            onChange={handleChange}
            placeholder="e.g. 13:45:00"
            required
          />
        </div>
        <div className="form-group">
          <label>Cost</label>
          <input
            name="cost"
            type="number"
            min="0"
            step="any"
            value={form.cost}
            onChange={handleChange}
            required
          />
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Offer Flight</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
