// client/src/components/PeopleOnGround.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function PeopleOnGround() {
  const [rows, setRows]       = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState('');

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/people_on_ground');
      setRows(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to load people on ground');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="view-container">
      <h2>People on the Ground</h2>
      <button
        className="refresh-btn"
        onClick={fetchData}
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Refresh'}
      </button>
      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Departing From</th>
            <th>Airport ID</th>
            <th>Airport Name</th>
            <th>City</th>
            <th>State</th>
            <th>Country</th>
            <th># Pilots</th>
            <th># Passengers</th>
            <th># Total</th>
            <th>Person List</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, idx) => (
            <tr key={idx}>
              <td>{r.departing_from}</td>
              <td>{r.airport}</td>
              <td>{r.airport_name}</td>
              <td>{r.city}</td>
              <td>{r.state || '—'}</td>
              <td>{r.country}</td>
              <td>{r.num_pilots}</td>
              <td>{r.num_passengers}</td>
              <td>{r.joint_pilots_passengers}</td>
              <td>{r.person_list}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
