import React, { useState } from 'react';
import axios from 'axios';

export default function AddAirplaneForm() {
  const [form, setForm] = useState({
    airline_id: '',
    tail_num: '',
    seat_capacity: '',
    speed: '',
    location_id: '',
    plane_type: '',
    maintained: '', // optional (nullable)
    model: '',      // optional (nullable)
    neo: 'true'
  });

  const [msg, setMsg] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const submission = {
      ...form,
      seat_capacity: parseInt(form.seat_capacity),
      speed: parseInt(form.speed),
      maintained: form.maintained === '' ? null : form.maintained,
      model: form.model === '' ? null : form.model,
      neo: form.neo === 'true'
    };

    try {
      await axios.post('/api/airplanes', submission);
      setMsg('✅ Airplane added successfully!');
    } catch (err) {
      setMsg(err.response?.data?.error || '❌ Error adding airplane');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>add_airplane()</h2>

      <label>Airline ID:</label>
      <input name="airline_id" value={form.airline_id} onChange={handleChange} required />

      <label>Tail Num:</label>
      <input name="tail_num" value={form.tail_num} onChange={handleChange} required />

      <label>Seat Cap:</label>
      <input name="seat_capacity" type="number" value={form.seat_capacity} onChange={handleChange} required />

      <label>Speed:</label>
      <input name="speed" type="number" value={form.speed} onChange={handleChange} required />

      <label>Location ID:</label>
      <input name="location_id" value={form.location_id} onChange={handleChange} required />

      <label>Plane Type:</label>
      <input name="plane_type" value={form.plane_type} onChange={handleChange} required />

      <label>Maintained (optional):</label>
      <input name="maintained" type="date" value={form.maintained} onChange={handleChange} />

      <label>Model (optional):</label>
      <input name="model" value={form.model} onChange={handleChange} />

      <label>Neo:</label>
      <select name="neo" value={form.neo} onChange={handleChange}>
        <option value="true">TRUE</option>
        <option value="false">FALSE</option>
      </select>

      <br /><br />
      <button type="submit">Add</button>
      {msg && <p>{msg}</p>}
    </form>
  );
}
