// client/src/components/ViewPersons.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function ViewPersons() {
  const [people, setPeople] = useState([]);
  const [error,  setError]  = useState('');

  const fetchPeople = () => {
    axios.get('/api/persons')
      .then(res => {
        setPeople(res.data);
        setError('');
      })
      .catch(() => {
        setError('Failed to load people');
      });
  };

  useEffect(fetchPeople, []);

  return (
    <div className="view-container">
      <h2>All People</h2>
      <button className="refresh-btn" onClick={fetchPeople}>Refresh</button>
      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Person ID</th>
            <th>First</th>
            <th>Last</th>
            <th>Location</th>
            <th>Flight</th>
            <th>Tax ID</th>
            <th>Experience</th>
            <th>Miles</th>
            <th>Funds</th>
          </tr>
        </thead>
        <tbody>
          {people.map((p, i) => (
            <tr key={i}>
              <td>{p.personID}</td>
              <td>{p.first_name}</td>
              <td>{p.last_name}</td>
              <td>{p.locationID}</td>
              <td>{p.commanding_flight || '—'}</td>
              <td>{p.taxID}</td>
              <td>{p.experience}</td>
              <td>{p.miles}</td>
              <td>{p.funds}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
