import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function ViewPassengerBoard() {
  const [rows, setRows] = useState([]);
  const [err,  setErr]  = useState('');

  const fetchRows = () => {
    axios.get('/api/passengers-board')
      .then(r => { setRows(r.data); setErr(''); })
      .catch(e => { setErr('Failed to load'); console.error(e); });
  };

  useEffect(fetchRows, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Passengers Boarded per Flight</h2>
      <button onClick={fetchRows} style={{ margin: '1rem 0' }}>ðŸ”„ Refresh</button>
      {err && <p style={{ color:'red' }}>{err}</p>}
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>FlightID</th>
            <th>#Â Passengers</th>
            <th>Passenger List</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r,i) => (
            <tr key={i}>
              <td>{r.flightID}</td>
              <td>{r.num_passengers}</td>
              <td>{r.passenger_list}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
