// AddAirportForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AddAirportForm.css';

export default function AddAirportForm() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    airport_id:     '',
    airport_name:   '',
    city:           '',
    state:          '',
    country:        '',
    location_id:    ''
  });
  const [msg, setMsg] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();

    const submission = {
      airport_id:    form.airport_id,
      airport_name:  form.airport_name,
      city:          form.city,
      state:         form.state,
      country:       form.country,
      location_id:   form.location_id
    };

    try {
      await axios.post('/api/airports', submission);
      navigate('/view-airports');
    } catch (err) {
      setMsg(err.response?.data?.error || 'Error adding airport');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>add_airport()</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Airport ID:</label>
          <input
            name="airport_id"
            value={form.airport_id}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Airport Name:</label>
          <input
            name="airport_name"
            value={form.airport_name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>City:</label>
          <input
            name="city"
            value={form.city}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>State:</label>
          <input
            name="state"
            value={form.state}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Country:</label>
          <input
            name="country"
            value={form.country}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Location ID:</label>
          <input
            name="location_id"
            value={form.location_id}
            onChange={handleChange}
            required
          />
        </div>
      </div>

      <div className="form-actions">
        <button type="submit">Add</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
