import React, { useState } from 'react';
import axios from 'axios';
import './Form.css';


export default function AddPersonForm() {

  /* role can be 'pilot' or 'passenger' */
  const [role, setRole] = useState('pilot');

  /* form state */
  const [form, setForm] = useState({
    personID:   '',
    first_name: '',
    last_name:  '',
    locationID: '',
    taxID:      '',
    experience: '',
    miles:      '',
    funds:      ''
  });

  const [msg, setMsg] = useState('');

  /* handle plain input changes */
  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  /* handle role switch + clear irrelevant fields */
  const handleRoleChange = newRole => {
    setRole(newRole);
    setForm(f => ({
      ...f,
      /* when switching to passenger, blank pilot‑only fields */
      taxID:      newRole === 'passenger' ? '' : f.taxID,
      experience: newRole === 'passenger' ? '' : f.experience,
      /* when switching to pilot, blank passenger‑only fields */
      miles:      newRole === 'pilot' ? '' : f.miles,
      funds:      newRole === 'pilot' ? '' : f.funds
    }));
  };



  /* helper to convert empty → null */
  const nullify = v => (v.trim() === '' ? null : v);

  /* validate combo per stored‑proc rules */
  const validate = () => {
    if (role === 'pilot') {
      return form.taxID && form.experience !== '' && !form.miles && !form.funds;
    }
    if (role === 'passenger') {
      return form.miles !== '' && form.funds !== '' && !form.taxID && !form.experience;
    }
    return false;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    /* 1 – front‑end rule check */
    if (!validate()) {
      setMsg('❌ Fill only the fields that apply to the chosen role.');
      return;
    }

    /* 2 – build payload */
    const submission = {
      personID:   form.personID,
      first_name: form.first_name,
      last_name:  nullify(form.last_name),
      locationID: form.locationID,
      taxID:      nullify(form.taxID),
      experience: form.experience === '' ? null : parseInt(form.experience, 10),
      miles:      form.miles      === '' ? null : parseInt(form.miles, 10),
      funds:      form.funds      === '' ? null : parseFloat(form.funds)
    };

    /* 3 – POST */
    try {
      const res = await axios.post('/api/persons', submission);

      /* 3a – success *only* if server said so */
      if (res.data && res.data.status === 'success') {
        navigate('/view_persons');           // auto‑switch
      } else {
        /* server replied 2xx but with an error payload */
        const msg = res.data?.error || 'Unknown server response.';
        setMsg(`❌ ${msg}`);
      }

    } catch (err) {
      /* 3b – network / 4xx / 5xx error */
      const msg = err.response?.data?.error || err.message;
      setMsg(`❌ ${msg}`);
    }
  };



  /* ───────────── JSX ───────────── */
  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>add_person()</h2>

      {/* role radio buttons */}
      <div style={{ marginBottom: '1rem' }}>
        <label>
          <input
            type="radio"
            name="role"
            value="pilot"
            checked={role === 'pilot'}
            onChange={() => handleRoleChange('pilot')}
          /> Pilot
        </label>
        &nbsp;&nbsp;
        <label>
          <input
            type="radio"
            name="role"
            value="passenger"
            checked={role === 'passenger'}
            onChange={() => handleRoleChange('passenger')}
          /> Passenger
        </label>
      </div>

      <div className="form-grid">
        {/* common person fields */}
        <div className="form-group">
          <label>Person ID:</label>
          <input name="personID" value={form.personID} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>First Name:</label>
          <input name="first_name" value={form.first_name} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Last Name (optional):</label>
          <input name="last_name" value={form.last_name} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Location ID:</label>
          <input name="locationID" value={form.locationID} onChange={handleChange} required />
        </div>

        {/* pilot‑only fields */}
        {role === 'pilot' && (
          <>
            <div className="form-group">
              <label>Tax ID:</label>
              <input name="taxID" value={form.taxID} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Experience (legs):</label>
              <input
                name="experience"
                type="number"
                value={form.experience}
                onChange={handleChange}
                required
              />
            </div>
          </>
        )}

        {/* passenger‑only fields */}
        {role === 'passenger' && (
          <>
            <div className="form-group">
              <label>Miles:</label>
              <input
                name="miles"
                type="number"
                value={form.miles}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Funds:</label>
              <input
                name="funds"
                type="number"
                step="0.01"
                value={form.funds}
                onChange={handleChange}
                required
              />
            </div>
          </>
        )}
      </div>

      <div className="form-actions">
        <button type="submit">Add</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
