import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Form.css';

export default function AssignPilot() {
  const [flightID, setFlightID]   = useState('');
  const [personID, setPersonID]   = useState('');
  const [msg, setMsg]             = useState('');
  const [isError, setIsError]     = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg('');
    setIsError(false);
    try {
      const res = await axios.post('/api/assign_pilot', {
        flightID,
        personID
      });
      if (res.status === 200 && res.data.status === 'success') {
        setFlightID('');
        setPersonID('');
        navigate('/flights');
      } else {
        setIsError(true);
        setMsg(res.data.error || 'Unknown error occurred');
      }
    } catch (err) {
      setIsError(true);
      setMsg(err.response?.data?.error || 'Unable to connect to server');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>assign_pilot()</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Flight ID</label>
          <input
            name="flightID"
            value={flightID}
            onChange={e => setFlightID(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Pilot (Person ID)</label>
          <input
            name="personID"
            value={personID}
            onChange={e => setPersonID(e.target.value)}
            required
          />
        </div>
      </div>
      <div className="form-actions">
        <button type="submit">Assign Pilot</button>
      </div>
      {msg && (
        <p className={isError ? 'error-text' : 'message'}>
          {msg}
        </p>
      )}
    </form>
  );
}
