// client/src/components/RecycleCrewForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './RecycleCrewForm.css';

export default function RecycleCrewForm() {
  const [flightID, setFlightID] = useState('');
  const [message, setMessage]   = useState(null);

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/recycle_crew', { flightID });
      setMessage('✅ Crew recycled!');
    } catch(err) {
      setMessage('❌ ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="form-container">
      <h2>Recycle Flight Crew</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Flight ID
          <input
            value={flightID}
            onChange={e => setFlightID(e.target.value)}
            required
          />
        </label>
        <button type="submit">Recycle</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
}
