// client/src/components/ToggleLicenseForm.jsx

import React, { useState } from 'react';
import axios from 'axios';
import './Form.css';

export default function ToggleLicenseForm() {
  const [form, setForm]   = useState({ personID: '', license: '' });
  const [msg, setMsg]     = useState('');
  const [error, setError] = useState('');

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/pilot_licenses/toggle', form);
      setMsg('✅ License status toggled');
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Error toggling license');
      setMsg('');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>toggle_license()</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Person ID</label>
          <input
            name="personID"
            value={form.personID}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>License</label>
          <input
            name="license"
            value={form.license}
            onChange={handleChange}
            required
          />
        </div>
      </div>
      <div className="form-actions">
        <button type="submit">Toggle License</button>
      </div>
      {msg   && <p className="message">{msg}</p>}
      {error && <p className="error-text">{error}</p>}
    </form>
  );
}
