import React, { useState } from 'react'; //


import axios from 'axios';
import './Form.css';
export default function ToggleLicenseForm() {
const [licenseData, setLicenseData] = useState({ personID: '', license: '' });
const [feedbackMsg, setFeedbackMsg] = useState('');
  const [errorMsg, setErrorMsg] = useState('');
  const handleChange = e => {
    const { name, value } = e.target;
    setLicenseData(prev => ({ ...prev, [name]: value }));
  };

const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('/api/pilot_licenses/toggle', licenseData);
    } catch (err) {
      setErrorMsg(err.response?.data?.error || 'Something went wrong while updating license');
      setFeedbackMsg('');
    }
  };
  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>Toggle Pilot License</h2>
      <div className="form-grid">
        <div className="form-group">
          <label>Person ID</label>
          <input
            name="personID"
            value={licenseData.personID}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>License</label>
          <input
            name="license"
            value={licenseData.license}
            onChange={handleChange}
            required
          />
        </div>
      </div>
      <div className="form-actions">
        <button type="submit">Update License Status</button>
      </div>
      {feedbackMsg && <p className="message">{feedbackMsg}</p>}
      {errorMsg && <p className="error-text">{errorMsg}</p>}
    </form>
  );
}
