import React, { useState } from 'react';
import './AddFlightTakeoffForm.css'; 

const AddPassengerBoardForm = () => {
  const [formData, setFormData] = useState({
    flightID: '',
  });
  const [response, setResponse] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('/api/passengers-board', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const result = await res.json();
      setResponse(result.message || result.error);
    } catch (error) {
      setResponse('Error connecting to server.');
    }
  };

  return (
    <div className="form-container">
      <h2>Passengers_board()</h2>
      <form onSubmit={handleSubmit}>
        <label>Airline ID:</label>
        <input name="airlineID" value={formData.airlineID} onChange={handleChange} />

        <button type="submit">Submit</button>
      </form>
      {response && (
        <div className={response.toLowerCase().includes("success") ? "success-message" : "error-message"}>
          {response}
        </div>
      )}
    </div>
  );
};

export default AddPassengerBoardForm;
