// client/src/components/FlightsOnGround.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function FlightsOnGround() {
  const [rows, setRows]     = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]   = useState('');

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/flights_on_ground');
      setRows(res.data);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to load flights on ground');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="view-container">
      <h2>Flights on the Ground</h2>
      <button
        className="refresh-btn"
        onClick={fetchData}
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Refresh'}
      </button>
      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Departing From</th>
            <th># Flights</th>
            <th>Flights List</th>
            <th>Earliest Arrival</th>
            <th>Latest Arrival</th>
            <th>Airplanes</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.departing_from}</td>
              <td>{r.num_flights}</td>
              <td>{r.flight_list}</td>
              <td>{r.earliest_arrival}</td>
              <td>{r.latest_arrival}</td>
              <td>{r.airplane_list}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
