import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AddAirplaneForm.css';          // reuse the same card/grid css

export default function AddPersonForm() {
  const navigate = useNavigate();

  /* local state */
  const [role, setRole] = useState('pilot');   // 'pilot' | 'passenger'
  const [form, setForm] = useState({
    personID:   '',
    firstName:  '',
    lastName:   '',
    locationID: '',
    taxID:      '',
    experience: '',
    miles:      '',
    funds:      ''
  });
  const [msg, setMsg] = useState('');

  /* helpers */
  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const nullify = v => (v.trim() === '' ? null : v);

  const validate = () => {
    if (role === 'pilot') {
      return form.taxID && form.experience !== '' && !form.miles && !form.funds;
    }
    if (role === 'passenger') {
      return form.miles !== '' && form.funds !== '' && !form.taxID && !form.experience;
    }
    return false;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!validate()) {
      setMsg('❌ Fill only the fields that apply to the chosen role.');
      return;
    }

    const submission = {
      personID:   form.personID,
      firstName:  form.firstName,
      lastName:   nullify(form.lastName),
      locationID: form.locationID,
      taxID:      nullify(form.taxID),
      experience: form.experience === '' ? null : parseInt(form.experience, 10),
      miles:      form.miles      === '' ? null : parseInt(form.miles, 10),
      funds:      form.funds      === '' ? null : parseFloat(form.funds)
    };

    try {
      await axios.post('/api/persons', submission);
      setMsg('✅ Person added!');
      // optionally navigate: navigate('/view-persons');
    } catch (err) {
      setMsg(err.response?.data?.error || 'Error adding person');
    }
  };

  return (
    <form className="form-container" onSubmit={handleSubmit}>
      <h2>add_person()</h2>

      {/* Role switch */}
      <div style={{ marginBottom: '1rem' }}>
        <label>
          <input
            type="radio"
            name="role"
            value="pilot"
            checked={role === 'pilot'}
            onChange={() => setRole('pilot')}
          /> Pilot
        </label>
        &nbsp;&nbsp;
        <label>
          <input
            type="radio"
            name="role"
            value="passenger"
            checked={role === 'passenger'}
            onChange={() => setRole('passenger')}
          /> Passenger
        </label>
      </div>

      <div className="form-grid">
        {/* basic person fields */}
        <div className="form-group">
          <label>Person ID:</label>
          <input name="personID" value={form.personID} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>First Name:</label>
          <input name="firstName" value={form.firstName} onChange={handleChange} required/>
        </div>
        <div className="form-group">
          <label>Last Name (optional):</label>
          <input name="lastName" value={form.lastName} onChange={handleChange}/>
        </div>
        <div className="form-group">
          <label>Location ID:</label>
          <input name="locationID" value={form.locationID} onChange={handleChange} required/>
        </div>

        {/* pilot‑only */}
        {role === 'pilot' && (
          <>
            <div className="form-group">
              <label>Tax ID:</label>
              <input name="taxID" value={form.taxID} onChange={handleChange} required/>
            </div>
            <div className="form-group">
              <label>Experience (legs):</label>
              <input
                name="experience"
                type="number"
                value={form.experience}
                onChange={handleChange}
                required
              />
            </div>
          </>
        )}

        {/* passenger‑only */}
        {role === 'passenger' && (
          <>
            <div className="form-group">
              <label>Miles:</label>
              <input
                name="miles"
                type="number"
                value={form.miles}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Funds:</label>
              <input
                name="funds"
                type="number"
                step="0.01"
                value={form.funds}
                onChange={handleChange}
                required
              />
            </div>
          </>
        )}
      </div>

      <div className="form-actions">
        <button type="submit">Add</button>
      </div>

      {msg && <p className="message">{msg}</p>}
    </form>
  );
}
