// client/src/components/ViewPersons.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function ViewPersons() {
  const [people, setPeople] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchPeople = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/persons');
      setPeople(res.data);
      setError('');
    } catch (err) {
      console.error('❌ Fetch persons error:', err);
      setError(err.response?.data?.error || 'Failed to load people');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPeople();
  }, []);

  return (
    <div className="view-container">
      <h2>View People</h2>
      <button
        className="refresh-btn"
        onClick={fetchPeople}
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Refresh'}
      </button>

      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Person ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Location ID</th>
            <th>Tax ID</th>
            <th>Experience</th>
            <th>Miles</th>
            <th>Funds</th>
          </tr>
        </thead>
        <tbody>
          {people.map((p, idx) => (
            <tr key={`${p.personID}-${idx}`}>
              <td>{p.personID}</td>
              <td>{p.first_name}</td>
              <td>{p.last_name || '—'}</td>
              <td>{p.locationID || '—'}</td>
              <td>{p.taxID}</td>
              <td>{p.experience}</td>
              <td>{p.miles != null ? p.miles : '—'}</td>
              <td>{p.funds != null ? p.funds : '—'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
