import React, { useState } from 'react';
import axios from 'axios';

export default function AddAirplaneForm() {
  const [form, setForm] = useState({
    tail_num: '',
    airline_id: '',
    speed: '',
    seat_capacity: '',
    maintained: '', // optional
    neo: '',
    model: '',
    plane_type: '',
    location_id: ''
  });
  const [msg, setMsg] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ ...form, [name]: type === 'checkbox' ? checked : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Create copy of form and handle optional fields
    const submission = { ...form };
    if (submission.maintained === '') {
      submission.maintained = null;
    }

    try {
      await axios.post('/api/airplanes', submission);
      setMsg('✅ Airplane added successfully!');
    } catch (err) {
      setMsg(err.response?.data?.error || '❌ Error adding airplane');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add Airplane</h2>
      <label>Tail Number:</label>
      <input name="tail_num" value={form.tail_num} onChange={handleChange} required />

      <label>Airline ID:</label>
      <input name="airline_id" value={form.airline_id} onChange={handleChange} required />

      <label>Speed:</label>
      <input name="speed" type="number" value={form.speed} onChange={handleChange} required />

      <label>Seat Capacity:</label>
      <input name="seat_capacity" type="number" value={form.seat_capacity} onChange={handleChange} required />

      <label>Date Maintained (optional):</label>
      <input name="maintained" type="date" value={form.maintained} onChange={handleChange} />

      <label>Neo (true/false):</label>
      <select name="neo" value={form.neo} onChange={handleChange} required>
        <option value="">Select</option>
        <option value="true">True</option>
        <option value="false">False</option>
      </select>

      <label>Model:</label>
      <input name="model" value={form.model} onChange={handleChange} required />

      <label>Plane Type:</label>
      <input name="plane_type" value={form.plane_type} onChange={handleChange} required />

      <label>Location ID:</label>
      <input name="location_id" value={form.location_id} onChange={handleChange} required />

      <button type="submit">Add</button>
      {msg && <p>{msg}</p>}
    </form>
  );
}
