import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddAirplaneForm.css';

export default function ViewAirports() {
  const [airports, setAirports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchAirports = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('/api/airports');
      setAirports(res.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to load airports');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAirports();
  }, []);

  return (
    <div className="view-container">
      <h2>View Airports</h2>
      <button
        className="refresh-btn"
        onClick={fetchAirports}
        disabled={loading}
      >
        {loading ? 'Loading…' : 'Refresh'}
      </button>

      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Airport ID</th>
            <th>Name</th>
            <th>City</th>
            <th>State</th>
            <th>Country</th>
            <th>Location ID</th>
          </tr>
        </thead>
        <tbody>
          {airports.map((a, idx) => (
            <tr key={`${a.airportID}-${idx}`}>
              <td>{a.airportID}</td>
              <td>{a.airport_name}</td>
              <td>{a.city}</td>
              <td>{a.state}</td>
              <td>{a.country}</td>
              <td>{a.locationID || '—'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
