// client/src/components/RouteSummary.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

export default function RouteSummary() {
  const [rows, setRows]     = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError]   = useState('');

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get('/api/route_summary');
      setRows(res.data);
      setError('');
    } catch (err) {
      console.error('❌ Fetch route_summary error:', err);
      setError(err.response?.data?.error || 'Failed to load route summary');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="view-container">
      <h2>Route Summary</h2>
      <button
        className="refresh-btn"
        onClick={fetchData}
        disabled={loading}
      >
        {loading ? 'Loading…' : '🔄 Refresh'}
      </button>

      {error && <p className="error-text">{error}</p>}

      <table className="data-table">
        <thead>
          <tr>
            <th>Route</th>
            <th># Legs</th>
            <th>Leg Sequence</th>
            <th>Total Distance</th>
            <th># Flights</th>
            <th>Flight List</th>
            <th>Airport Sequence</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, idx) => (
            <tr key={idx}>
              <td>{r.route}</td>
              <td>{r.num_legs}</td>
              <td>{r.leg_sequence}</td>
              <td>{r.route_length}</td>
              <td>{r.num_flights}</td>
              <td>{r.flight_list}</td>
              <td>{r.airport_sequence}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
